						<button className="btn btn-sm btn-outline-secondary py-0 small ">
							✎
						</button>
						<a className="btn btn-sm btn-outline-danger py-0 small opacity-75">
							×
						</a>	
					</span>
				</div>
			)}
		</div>
    );
}


export default TaskCard;
