    constructor( _id = null, 
                 _name = null,               
                 _createdAt = null, 
                 _doneAt = null, 
                 _deletedAt = null
    ){
        if ( _id !== null ) this.id = _id;
        if ( _name !== null ) this.name = _name;
        this.createdAt = 
            _createdAt === null ? new Date () : new Date (_createdAt);
        this.doneAt = 
            _doneAt === null ? null : new Date( _doneAt );
        this.deletedAt = 
            _deletedAt === null ? null : new Date ( _deletedAt );
    }

    timeElapsed(date) {

            let timeDiff = new Date () - date;
            // strip the ms
            timeDiff /= 1000;

            if( timeDiff >86400 ){
                return "Hace " + Math.round(timeDiff /86400) + " dia";
            }
            if( timeDiff >3600 ){
                return "Hace " + Math.round(timeDiff /3600) + "hrs.";
            }
            if( timeDiff >60 ){
                return "Hace " + Math.round(timeDiff /60) + " min.";
            }

            // get seconds
            return "Hace" +Math.round(timeDiff) + " seg.";

    }

    getDateTimeString(date ) {
        return (
            String(date.toLocalDateString()) + " " + String(date.toLocaleTime)
        )
    }
}
